#include <torch/extension.h>
#include <cuda_runtime.h>
#include <cuda.h>
#include <cmath>
#include <vector>
#include <ATen/cuda/CUDAContext.h>

// Sigmoid CUDA 内核实现
__global__ void sigmoid_kernel(const float* input, float* output, int64_t num_elements) {
    int64_t idx = blockIdx.x * blockDim.x + threadIdx.x;

    if (idx < num_elements) {
        float val = input[idx];
        // Sigmoid 函数: 1 / (1 + exp(-x))
        output[idx] = 1.0f / (1.0f + expf(-val));
    }
}

// C++ Wrapper 实现
torch::Tensor kb_21_Sigmoid_wrapper(torch::Tensor arg0) {
    // 验证输入
    TORCH_CHECK(arg0.is_cuda(), "Input tensor must be on GPU");
    TORCH_CHECK(arg0.scalar_type() == torch::kFloat32, "Input tensor must be float32");

    // 分配输出张量
    auto output = torch::empty_like(arg0);

    // 获取张量数据指针
    const float* input_ptr = arg0.data_ptr<float>();
    float* output_ptr = output.data_ptr<float>();

    // 计算总元素数量
    int64_t num_elements = arg0.numel();

    // 计算网格和块维度
    int threads_per_block = 256;
    int blocks_per_grid = (num_elements + threads_per_block - 1) / threads_per_block;

    // 获取当前CUDA流
    cudaStream_t stream = at::cuda::getCurrentCUDAStream();

    // 启动内核
    sigmoid_kernel<<<blocks_per_grid, threads_per_block, 0, stream>>>(
        input_ptr, output_ptr, num_elements
    );

    // 检查内核执行是否成功
    C10_CUDA_KERNEL_LAUNCH_CHECK();

    return output;
}
